      <script type="text/javascript">
    Shopify.updateCartInfo = function(cart, cart_summary_id, cart_count_id) {
        if ((typeof cart_summary_id) === 'string') {
            var cart_summary = jQuery(cart_summary_id);
            if (cart_summary.length) {

                cart_summary.empty();


                jQuery.each(cart, function(key, value) {
                    if (key === 'items') {

                        if (value.length) {

                            jQuery('<div class="items"></div>').appendTo(cart_summary);
                            var table = jQuery(cart_summary_id + ' div.items');

                            jQuery.each(value, function(i, item) {

                                jQuery('<div class="items-inner"> \
                                    <div class="cart-item-image"> \
                                        <a href="' + item.url + '">\
                                            <img class="lazyload" data-src="' + Shopify.resizeImage(item.image, '90x') + '" alt="" />\
                                        </a>\
                                    </div>\
                                    <div class="cart-item-info">\
                                        <div class="cart-item-title">\
                                            <a href="' + item.url + '">' + item.product_title + '</a>\
                                        </div>\
										<div class="cart-item-quantity">QTY: ' + item.quantity  + '</div>\
                                        <div class="cart-item-price">' + Shopify.formatMoney(item.price, '<span class=money>Rp {{ amount }}</span>')  + '</div>\
                                    </div>\
									<a data-cart-item="' + item.id + '" class="cart-close" title="Hapus" href="javascript:;" data-cart-remove="' +  (i + 1) + '"><i class="demo-icon icon-close" aria-hidden="true"></i></a>\
                                    </div>').appendTo(table);
                            });

                            jQuery('<div class="subtotal"><span>Total</span><span class="cart-item-total-price">' + Shopify.formatMoney(cart.total_price, '<span class=money>Rp {{ amount }}</span>') + '</span></div>').appendTo(cart_summary);
                            jQuery('<div class="action"><a href="/cart\" class="btn btn-1">Lihat Keranjang</a><button class="btn btn-1" onclick="window.location=\'/checkout\'">Lanjut ke Checkout</button></div>').appendTo(cart_summary);


                        }
                        else {
                            jQuery('<div class="cart-item-empty"><p>Saat ini keranjang masih kosong</p></div>').appendTo(cart_summary);
                        }
                    }
                });


            }
        }

        updateCartDesc(cart);
    };

    function updateCartDesc(data){
        var $cartLinkText = $('.cart-target .number');
      	var $cartPrice = ' - '+ Shopify.formatMoney(data.total_price, '<span class=money>Rp {{ amount }}</span>');
        
        switch(data.item_count){
             case 0:
               $cartLinkText.html('<span class="n-item">0</span>');
               break;
             case 1:
               $cartLinkText.html('<span class="n-item">1</span>');
               break;
             default:
               $cartLinkText.html('<span class="n-item">' + data.item_count + '</span>');
               break;
        }
      
        //$cartLinkText.append($cartPrice);

       	$('.addcart-modal-subtotal').html('<strong>Total:&nbsp;</strong>' +Shopify.formatMoney(data.total_price, '<span class=money>Rp {{ amount }}</span>'));

      	// Update free shipping
        var _cart_total = data.total_price
            ,_free_shipping_price = 500000
            ,_free_shipping_price_100 = 500000*100;
                                
        if (_cart_total > _free_shipping_price_100){
          $('.sb-progress').html('<span class="sb-progress-bar" style="width: 100%">100%</span>');
          $('.sb-text').html('Free shipping for orders over ' + Shopify.formatMoney(_free_shipping_price_100, "<span class=money>Rp {{ amount }}</span>"));
        }
                                                                                                                
        else{
          var _progress = Math.floor(_cart_total / _free_shipping_price)
              ,_spend = _free_shipping_price_100 - _cart_total;
                                                                                                          
          $('.sb-progress').html('<span class="sb-progress-bar" style="width:' + _progress +'%">' + _progress + '%</span>');
          $('.sb-text').html('Spend ' + Shopify.formatMoney(_spend, "<span class=money>Rp {{ amount }}</span>") + ' reach FREE SHIPPING! Continue shopping to add more products to your cart and receive free shipping for orders over ' + Shopify.formatMoney(_free_shipping_price_100, "<span class=money>Rp {{ amount }}</span>"));
        }
                                                            
        
    }

    Shopify.onCartUpdate = function(cart) {
        Shopify.updateCartInfo(cart, '#cart-info #cart-content', 'shopping-cart');
    };

    $(window).on('load', function () {

        Shopify.getCart(function(cart) {
            Shopify.updateCartInfo(cart, '#cart-info #cart-content');	
        });
      
      	$('.cart-dd').on('click', '.cart-close', function(e){
            var prodId = $(e.currentTarget).data('cart-item');
            $('body').addClass('cart-loading');
            $.ajax({
                type: 'POST',
                url: '/cart/change.js',
                data: { quantity: 0, id: prodId },
                dataType: 'json',
                error: function(err){
                    console.log(err);
                },
                success: function(resp) {
                	$('body').removeClass('cart-loading');
                    Shopify.updateCartInfo(resp, '#cart-info #cart-content');
                    if(typeof cartObj != "undefined") {
                        cartObj.syncCartSB(resp, prodId)
                    } else {
                         
                    }
                                            
                    // Update free shipping
                    var _cart_total = resp.total_price
                        ,_free_shipping_price = 500000
                        ,_free_shipping_price_100 = 500000*100;
                                
                   	if (_cart_total > _free_shipping_price_100){
                      $('.sb-progress').html('<span class="sc-progress-bar" style="width: 100%">100%</span>');
                      $('.sb-text').html('Free shipping for orders over ' + Shopify.formatMoney(_free_shipping_price_100, "<span class=money>Rp {{ amount }}</span>"));
                   	}
                                                                                                                
                    else{
                      var _progress = Math.floor(_cart_total / _free_shipping_price)
                       	  ,_spend = _free_shipping_price_100 - _cart_total;
                      $('.sb-progress').html('<span class="sb-progress-bar" style="width:' + _progress +'%">' + _progress + '%</span>');
                      $('.sb-text').html('Spend ' + Shopify.formatMoney(_spend, "<span class=money>Rp {{ amount }}</span>") + ' reach FREE SHIPPING! Continue shopping to add more products to your cart and receive free shipping for orders over ' + Shopify.formatMoney(_free_shipping_price_100, "<span class=money>Rp {{ amount }}</span>"));
                    }
                }
            })
        })
    });
</script>
